import { useLocation, useNavigate, useParams } from "react-router-dom";
import { DAYS, norm } from "../data";
import { useEffect, useMemo, useRef, useState } from "react";

// ----- Ïú†Ìã∏: Îã®Ïñ¥ Ï†ïÍ∑úÌôî -----
const splitWords = (s) => norm(s).split(" ").filter(Boolean);

// ----- Ïú†Ìã∏: Ï†ïÎãµ Î¨∏Ïû•ÏùÑ korChunks ÎπÑÏú®Î°ú "ÏòÅÏñ¥ Ï≤≠ÌÅ¨"Î°ú ÏûêÎ•¥Í∏∞ -----
function chunkAnswerByKor(answer, korChunks) {
  const words = splitWords(answer);
  if (!korChunks?.length) return [words];

  // korChunks Í∏∏Ïù¥(Î¨∏Ïûê Ïàò) ÎπÑÏú®Î°ú Îã®Ïñ¥ Ïàò Î∞∞Î∂Ñ
  const lens = korChunks.map(c => (c || "").length || 1);
  const totalLen = lens.reduce((a,b)=>a+b,0);
  const totalWords = words.length;

  // Í∞Å Ï≤≠ÌÅ¨Ïóê Îã®Ïñ¥ Í∞úÏàò Î∞∞Ï†ï(Î∞òÏò¨Î¶º ÎàÑÏ†Å Î≥¥Ï†ï)
  const alloc = [];
  let used = 0;
  for (let i=0;i<lens.length;i++){
    let n = Math.round(totalWords * (lens[i]/totalLen));
    // ÎßàÏßÄÎßâ Ï≤≠ÌÅ¨Ïóê ÎÇ®ÏùÄ Îã®Ïñ¥ Î™∞ÏïÑÏ£ºÍ∏∞
    if (i === lens.length-1) n = totalWords - used;
    alloc.push(Math.max(0,n));
    used += n;
  }
  // ÏûêÎ•¥Í∏∞
  const chunks = [];
  let idx = 0;
  for (const n of alloc){
    chunks.push(words.slice(idx, idx+n));
    idx += n;
  }
  return chunks;
}

// ----- Ïú†Ìã∏: Ï≤≠ÌÅ¨/Îã®Ïñ¥ Ï±ÑÏ†ê -----
function scoreByChunks(user, answer, korChunks) {
  const u = splitWords(user);
  const a = splitWords(answer);

  // ÏúÑÏπòÍ∏∞Î∞ò Îã®Ïñ¥ Ï±ÑÏ†ê(Í∞ÑÎã®ÌïòÍ≤å Ìè¨ÏßÄÏÖò Îß§Ïπ≠)
  const wordResults = a.map((w,i)=>({ word:w, ok: u[i] === w }));

  // Ï≤≠ÌÅ¨Î°ú Îã§Ïãú Î¨∂Í∏∞
  const aChunks = chunkAnswerByKor(answer, korChunks);
  const byChunk = [];
  let cursor = 0;
  for (const ch of aChunks){
    const wr = wordResults.slice(cursor, cursor + ch.length);
    const ok = wr.filter(x=>x.ok).length;
    byChunk.push({
      words: wr,
      ok,
      total: ch.length
    });
    cursor += ch.length;
  }

  const totalOk = wordResults.filter(x=>x.ok).length;
  const total = wordResults.length || 1;
  return { byChunk, totalOk, total, pct: Math.round(totalOk/total*100) };
}

export default function Exam() {
  const { state } = useLocation();
  const { day } = useParams();
  const nav = useNavigate();
  const items = DAYS[day] || [];

  const [idx, setIdx] = useState(0);
  const [phase, setPhase] = useState("speak"); // speak -> type
  const [input, setInput] = useState("");
  const [show, setShow] = useState(false);
  const [recognizing, setRecognizing] = useState(false);

  // ÏÑ∏ÏÖò ÎàÑÏ†Å
  const sessionRef = useRef({
    name: state?.name || "",
    date: state?.date || "",
    day,
    answers: [] // {idx, kor, answer, user, phase, score}
  });

  useEffect(()=>{
    if (!state?.name) nav("/");
  }, [state, nav]);

  const current = items[idx];
  const answer = current?.eng || "";
  const kor = current?.korChunks || [];

  const result = useMemo(
    ()=> show ? scoreByChunks(input, answer, kor) : null,
    [show, input, answer, kor]
  );

  // ÎßêÌïòÍ∏∞(STT)
  const handleSpeak = () => {
    const SR = window.SpeechRecognition || window.webkitSpeechRecognition;
    if (!SR) { alert("ÌÅ¨Î°¨ÏóêÏÑúÎßå ÎßêÌïòÍ∏∞Í∞Ä ÏßÄÏõêÎê©ÎãàÎã§."); return; }
    const recog = new SR();
    recog.lang = "en-US";
    recog.interimResults = false;
    recog.maxAlternatives = 1;
    recog.onstart = ()=> setRecognizing(true);
    recog.onend = ()=> setRecognizing(false);
    recog.onresult = (e)=>{
      const text = e.results[0][0].transcript;
      setInput(text);
      setShow(true);
    };
    recog.start();
  };

  // ÌòÑÏû¨ Î¨∏Ï†ú Ï†ÄÏû•
  const saveCurrent = (mode) => {
    sessionRef.current.answers.push({
      idx,
      kor: kor.join(" / "),
      answer,
      user: input,
      phase: mode,
      score: result
        ? { ok: result.totalOk, total: result.total, pct: result.pct }
        : { ok: 0, total: splitWords(answer).length, pct: 0 }
    });
  };

  const toTypePhase = () => {
    if (!show) { alert("Î®ºÏ†Ä Ï±ÑÏ†êÌïòÍ∏∞Î•º ÎàåÎü¨Ï£ºÏÑ∏Ïöî."); return; }
    saveCurrent("speak");
    setPhase("type");
    setInput("");
    setShow(false);
  };

  const nextItem = () => {
    if (!show) { alert("Î®ºÏ†Ä Ï±ÑÏ†êÌïòÍ∏∞Î•º ÎàåÎü¨Ï£ºÏÑ∏Ïöî."); return; }
    saveCurrent("type");
    if (idx + 1 < items.length) {
      setIdx(idx + 1);
      setPhase("speak");
      setInput("");
      setShow(false);
    } else {
      // ÏÑ∏ÏÖò Ï†ÄÏû• ÌõÑ Í≤∞Í≥º ÌéòÏù¥ÏßÄÎ°ú Ïù¥Îèô
      const all = JSON.parse(localStorage.getItem("bellcat:sessions") || "[]");
      const session = { ...sessionRef.current, createdAt: new Date().toISOString() };
      all.push(session);
      localStorage.setItem("bellcat:sessions", JSON.stringify(all));
      nav("/result", { state: { session } });
    }
  };

  return (
    <div className="container">
      <div className="card">
        <div className="chip" style={{marginBottom:8}}>Î¨∏Ï†ú {idx+1} / {items.length} ¬∑ {phase==="speak"?"ÎßêÌïòÍ∏∞":"Ïì∞Í∏∞"}</div>
        <div className="yellow" style={{ marginBottom: 12, fontWeight:600 }}>
          {kor.join(" / ")}
        </div>

        <textarea
          value={input}
          onChange={(e)=>setInput(e.target.value)}
          rows={2}
          placeholder="ÏòÅÏñ¥Î°ú ÏûÖÎ†• (Ïä¨ÎûòÏãú ÏóÜÏñ¥ÎèÑ Îê®)"
        />

        <div className="row" style={{justifyContent:"center", marginTop:10}}>
          {phase === "speak" && (
            <button className="btn purple" onClick={handleSpeak}>
              {recognizing ? "üé§ Îì£Îäî Ï§ë..." : "üéô ÎßêÌïòÍ∏∞"}
            </button>
          )}
          <button className="btn primary" onClick={()=>setShow(true)}>Ï±ÑÏ†êÌïòÍ∏∞</button>
          {phase === "speak" ? (
            <button className="btn success" onClick={toTypePhase}>Îã§Ïùå Îã®Í≥Ñ(Ïì∞Í∏∞)</button>
          ) : (
            <button className="btn success" onClick={nextItem}>Îã§Ïùå Î¨∏Ï†ú</button>
          )}
        </div>

        {show && result && (
          <div className="result" style={{marginTop:12}}>
            <b>Ï≤≠ÌÅ¨ Ï±ÑÏ†ê</b>
            <div style={{display:"grid", gap:8, marginTop:8}}>
              {result.byChunk.map((ch, i)=>(
                <div key={i} style={{border:"1px solid #eee", borderRadius:8, padding:8}}>
                  <div className="muted" style={{marginBottom:6}}>
                    KOR {i+1}: {kor[i] || "-"}
                  </div>
                  <div>
                    {ch.words.map((w, j)=>(
                      <span key={j} className={w.ok ? "word-ok" : "word-bad"}>
                        {w.word + " "}
                      </span>
                    ))}
                  </div>
                  <div className="muted">{ch.ok}/{ch.total}</div>
                </div>
              ))}
            </div>

            <div style={{marginTop:10, fontWeight:700}}>
              Ï¥ùÏ†ê: {result.totalOk}/{result.total} ({result.pct}%)
            </div>

            <details style={{marginTop:8}}>
              <summary className="muted">Ï†ïÎãµ Î¨∏Ïû• Î≥¥Í∏∞</summary>
              <div style={{marginTop:6}}>{answer}</div>
            </details>
          </div>
        )}
      </div>
    </div>
  );
}
