import { useLocation, useNavigate, useParams } from "react-router-dom";
import { DAYS } from "../data";
import { useEffect, useMemo, useRef, useState } from "react";
import { DiffText, scoreWords } from "../components/DiffText";

export default function Exam() {
  const { state } = useLocation();
  const { day } = useParams();
  const nav = useNavigate();
  const items = DAYS[day] || [];
  const [idx, setIdx] = useState(0);
  const [phase, setPhase] = useState("speak"); // speak -> type
  const [input, setInput] = useState("");
  const [show, setShow] = useState(false);
  const [recognizing, setRecognizing] = useState(false);
  const sessionRef = useRef({ name: state?.name || "", date: state?.date || "", day, answers: [] });

  useEffect(()=>{
    if (!state?.name) nav("/");
  }, [state, nav]);

  const current = items[idx];
  const answer = current?.eng || "";

  const handleSpeak = () => {
    const SR = window.SpeechRecognition || window.webkitSpeechRecognition;
    if (!SR) { alert("Chrome 브라우저에서만 가능해요"); return; }
    const recognition = new SR();
    recognition.lang = "en-US";
    recognition.interimResults = false;
    recognition.maxAlternatives = 1;
    recognition.onstart = () => setRecognizing(true);
    recognition.onend = () => setRecognizing(false);
    recognition.onresult = (e) => {
      const transcript = e.results[0][0].transcript;
      setInput(transcript);
      setShow(true);
    };
    recognition.start();
  };

  const score = useMemo(()=> show ? scoreWords(input, answer) : null, [show, input, answer]);

  const saveCurrent = (mode) => {
    sessionRef.current.answers.push({
      idx,
      kor: current.korChunks.join(" / "),
      answer,
      user: input,
      phase: mode,
      score: score || { ok: 0, total: answer.split(" ").length, pct: 0 }
    });
  };

  const toTypePhase = () => {
    if (!show) { alert("먼저 채점하기를 눌러주세요."); return; }
    saveCurrent("speak");
    setPhase("type");
    setInput("");
    setShow(false);
  };

  const nextItem = () => {
    if (!show) { alert("먼저 채점하기를 눌러주세요."); return; }
    saveCurrent("type");
    if (idx + 1 < items.length) {
      setIdx(idx + 1);
      setPhase("speak");
      setInput("");
      setShow(false);
    } else {
      const all = JSON.parse(localStorage.getItem("bellcat:sessions") || "[]");
      all.push({ ...sessionRef.current, createdAt: new Date().toISOString() });
      localStorage.setItem("bellcat:sessions", JSON.stringify(all));
      nav("/result", { state: { session: sessionRef.current } });
    }
  };

  return (
    <div className="container">
      <div className="card">
        <div className="yellow" style={{ marginBottom: 10 }}>
          {current.korChunks.join(" / ")}
        </div>
        <textarea
          value={input}
          onChange={(e)=>setInput(e.target.value)}
          rows={2}
          placeholder="영어로 입력 (슬래시 없어도 됨)"
        />
        <div className="row" style={{justifyContent:"center", marginTop:10}}>
          {phase === "speak" && (
            <button className="btn purple" onClick={handleSpeak}>
              {recognizing ? "🎤 듣는 중..." : "🎙 말하기"}
            </button>
          )}
          <button className="btn primary" onClick={()=>setShow(true)}>채점하기</button>
          {phase === "speak" ? (
            <button className="btn success" onClick={toTypePhase}>다음 단계(쓰기)</button>
          ) : (
            <button className="btn success" onClick={nextItem}>다음 문제</button>
          )}
        </div>
        {show && (
          <div className="result" style={{marginTop:12}}>
            <b>정답 비교</b><br/>
            <DiffText user={input} answer={answer} />
            {score && <div className="muted">점수: {score.ok}/{score.total} ({score.pct}%)</div>}
          </div>
        )}
      </div>
    </div>
  );
}
