// src/pages/Start.js
import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

export default function Start() {
  const nav = useNavigate();
  const [name, setName] = useState("");
  const [date, setDate] = useState(() =>
    new Date().toISOString().slice(0, 10)
  );

  const goExam = (day) => {
    if (!name.trim()) {
      alert("학생 이름을 입력해 주세요.");
      return;
    }
    nav(`/exam/${day}`, { state: { name, date, day } });
  };

  const goRecords = () => nav("/records");
  const goBadges = () => nav("/badges");

  const clearData = () => {
    if (
      window.confirm(
        "정말 모든 데이터(기록 + 통과배지)를 삭제할까요? 복구할 수 없어요."
      )
    ) {
      localStorage.removeItem("records");
      localStorage.removeItem("mastery_v1");
      alert("데이터를 삭제했어요.");
    }
  };

  return (
    <div className="container">
      <div className="card" style={{ width: "100%", maxWidth: 640 }}>
        <h1 className="title">Who’s going to bell the cat?</h1>
        <p className="subtitle">말하기 시험 · 오답 재시험 · 뱃지</p>

        <div style={{ display: "grid", gap: 12, marginTop: 12 }}>
          <input
            type="text"
            placeholder="학생 이름"
            value={name}
            onChange={(e) => setName(e.target.value)}
            style={{
              padding: 12,
              borderRadius: 10,
              border: "1px solid #2e2e2e",
              background: "transparent",
              color: "#fff",
            }}
          />
          <input
            type="date"
            value={date}
            onChange={(e) => setDate(e.target.value)}
            style={{
              padding: 12,
              borderRadius: 10,
              border: "1px solid #2e2e2e",
              background: "transparent",
              color: "#fff",
            }}
          />
        </div>

        <div className="nav" style={{ marginTop: 16, flexWrap: "wrap", gap: 8 }}>
          <button className="btn primary" onClick={() => goExam("day1")}>
            Day 1 시작
          </button>
          <button className="btn primary" onClick={() => goExam("day2")}>
            Day 2 시작
          </button>
          <button className="btn primary" onClick={() => goExam("day3")}>
            Day 3 시작
          </button>
          <button className="btn primary" onClick={() => goExam("day4")}>
            Day 4 시작
          </button>
        </div>

        <div className="nav" style={{ marginTop: 8, flexWrap: "wrap", gap: 8 }}>
          <button className="btn" onClick={goRecords}>기록 보기</button>
          <button className="btn" onClick={goBadges}>뱃지 보관함</button>
          <button className="btn danger" onClick={clearData}>데이터 전체 삭제</button>
        </div>
      </div>
    </div>
  );
}
