// src/pages/Exam.js
import React, { useMemo, useRef, useState } from "react";
import { useLocation, useNavigate, useParams } from "react-router-dom";
import { QUESTIONS } from "../data";
import { addRecord, uid, updateMasteryOnPerfect } from "../store";

const norm = (s) =>
  s.toLowerCase().replace(/[.,!?;:"'()]/g, "").replace(/\s+/g, " ").trim();
const tokenize = (s) => norm(s).split(" ").filter(Boolean);

export default function Exam() {
  const nav = useNavigate();
  const { day } = useParams();
  const { state } = useLocation(); // { name, date, questions? (오답만) }
  const name = state?.name || "";
  const date = state?.date || new Date().toISOString().slice(0,10);

  // 오답-재시험이면 state.questions 사용, 아니면 QUESTIONS[day]
  const baseList = useMemo(() => {
    if (state?.questions && Array.isArray(state.questions) && state.questions.length) {
      return state.questions;
    }
    return QUESTIONS[day] || [];
  }, [day, state?.questions]);

  const [idx, setIdx] = useState(0);
  const [ans, setAns] = useState("");
  const q = baseList[idx];

  const inputRef = useRef(null);

  if (!q) {
    return (
      <div className="container">
        <div className="card">
          <h2>문제가 없어요</h2>
          <div className="nav">
            <button className="btn" onClick={()=>nav("/")}>처음으로</button>
          </div>
        </div>
      </div>
    );
  }

  const expectedTokens = tokenize(q.enChunks.join(" "));
  const userTokens = tokenize(ans);

  const colored = expectedTokens.map((tk, i) => {
    const user = userTokens[i] || "";
    const ok =
      user === tk || user + "s" === tk || user === tk + "s";
    return { tk, ok };
  });

  const handleScore = () => {
    const wrongIdxs = [];
    expectedTokens.forEach((tk, i) => {
      const user = userTokens[i] || "";
      if (user === tk || user + "s" === tk || user === tk + "s") return;
      wrongIdxs.push(i);
    });

    const score = expectedTokens.length - wrongIdxs.length;

    const rec = {
      id: uid(),
      type: "SPEAK",
      name, date, day,
      qid: q.id,
      koChunks: q.koChunks,
      enChunks: expectedTokens,
      full: q.full,
      user: ans,
      wrongIdxs,
      totalChunks: expectedTokens.length,
      score,
      ts: Date.now(),
    };
    addRecord(rec);

    // 오답 0 → 숙련도/뱃지 체크
    const totalQuestionsOfDay =
      (QUESTIONS[day] || []).length || (state?.originTotal ?? baseList.length);
    if (wrongIdxs.length === 0) {
      const granted = updateMasteryOnPerfect(name, day, q.id, totalQuestionsOfDay);
      if (granted) {
        // 간단 토스트
        alert(`🎉 ${name} — ${String(day).toUpperCase()} 뱃지 획득!`);
      }
    }

    // 다음 문항
    setAns("");
    if (idx < baseList.length - 1) {
      setIdx(idx + 1);
      setTimeout(()=>inputRef.current?.focus(), 0);
    } else {
      nav("/records", { state: { name, day } });
    }
  };

  return (
    <div className="container">
      <div className="card">
        <h1 className="title">시험 · 문제 {idx + 1} / {baseList.length}</h1>
        <p className="yellow">{q.koChunks.join(" / ")}</p>

        <div className="row" style={{flexWrap:"wrap", gap:8, margin:"12px 0"}}>
          {colored.map(({tk, ok}, i) => (
            <span key={i} className={ok ? "word-ok" : "word-bad"}>{tk}</span>
          ))}
        </div>

        <input
          ref={inputRef}
          value={ans}
          onChange={e=>setAns(e.target.value)}
          placeholder="말하거나(녹음) / 또는 타이핑해도 됨"
          onKeyDown={(e)=>{ if (e.key==="Enter") handleScore(); }}
        />

        <div className="nav">
          <button className="btn primary" onClick={handleScore}>채점하기</button>
          <button className="btn" onClick={()=>setAns("")}>지우기</button>
          <button className="btn" onClick={()=>nav("/records")}>기록 보기</button>
        </div>
      </div>
    </div>
  );
}
