import React, { useEffect, useMemo, useRef, useState } from "react";
import { useLocation, useNavigate, useParams } from "react-router-dom";
import { QUESTIONS } from "../data";

// ÏÜåÎ¨∏Ïûê/Í∏∞Ìò∏ Ï†úÍ±∞, Í≥µÎ∞± Ï†ïÎ¶¨
const norm = (s) =>
  (s || "")
    .toLowerCase()
    .replace(/[.,!?;:"'()]/g, "")
    .replace(/\s+/g, " ")
    .trim();

const tokenize = (s) => norm(s).split(" ").filter(Boolean);

export default function Exam() {
  const nav = useNavigate();
  const { day } = useParams(); // URLÏùò :day (Ïòà: 1, 2, 3, 4)
  const { state } = useLocation(); // HomeÏóêÏÑú ÎÑòÍ≤®Ï§Ä { name }
  const studentName = state?.name || "";

  // data.jsÍ∞Ä day1, day2 Ï≤òÎüº ÎêòÏñ¥ ÏûàÎã§Í≥† Í∞ÄÏ†ï
  const dayKey = useMemo(() => `day${day}`, [day]);
  const list = useMemo(() => QUESTIONS[dayKey] || [], [dayKey]);

  const [idx, setIdx] = useState(0);
  const q = list[idx];

  // ÎßêÌïòÍ∏∞ Í¥ÄÎ†®
  const [recognized, setRecognized] = useState(""); // Ïù∏ÏãùÎêú Î¨∏Ïû•
  const [checking, setChecking] = useState(false);  // Ï±ÑÏ†ê Ï§ë/ÌõÑ ÏÉÅÌÉú ÌëúÏãúÏö© Ìä∏Î¶¨Í±∞
  const [result, setResult] = useState(null);       // { wrongIdxs, expectedTokens, userTokens, score, total }
  const recogRef = useRef(null);

  // ÏùåÏÑ±Ïù∏Ïãù Ï§ÄÎπÑ
  useEffect(() => {
    const R = window.SpeechRecognition || window.webkitSpeechRecognition;
    if (!R) {
      recogRef.current = null;
      return;
    }
    const recog = new R();
    recog.lang = "en-US";
    recog.interimResults = false;
    recog.maxAlternatives = 1;

    recog.onresult = (e) => {
      const text = e.results?.[0]?.[0]?.transcript || "";
      setRecognized(text);
      // ÎßêÌïòÍ∏∞Í∞Ä ÎÅùÎÇòÎ©¥ ÏûêÎèôÏúºÎ°ú Ï±ÑÏ†ê
      handleCheck(text);
    };
    recog.onerror = () => {
      setRecognized("");
    };
    recogRef.current = recog;
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  // ÎßêÌïòÍ∏∞ ÏãúÏûë
  const handleSpeak = () => {
    if (!recogRef.current) {
      alert("Ïù¥ Î∏åÎùºÏö∞Ï†ÄÎäî ÏùåÏÑ± Ïù∏ÏãùÏùÑ ÏßÄÏõêÌïòÏßÄ ÏïäÏïÑÏöî. ChromeÏùÑ ÏÇ¨Ïö©Ìï¥ Ï£ºÏÑ∏Ïöî!");
      return;
    }
    setRecognized("");
    setResult(null);
    setChecking(false);
    try {
      recogRef.current.start();
    } catch (_) {
      // Safari Îì±ÏóêÏÑú start Ï§ëÎ≥µ Ìò∏Ï∂ú ÏòàÏô∏ Î¨¥Ïãú
    }
  };

  // Ï±ÑÏ†ê: enChunksÎ•º Í∏∞Ï§ÄÏúºÎ°ú ÌÜ†ÌÅ∞ ÎπÑÍµê
  const handleCheck = (textMaybe) => {
    const text = typeof textMaybe === "string" ? textMaybe : recognized;
    const expectedTokens = tokenize((q?.enChunks || []).join(" "));
    const userTokens = tokenize(text);
    const wrongIdxs = [];

    expectedTokens.forEach((exp, i) => {
      const user = userTokens[i] || "";
      if (user === exp) return;
      // s/es Í∞ÄÎ≤ºÏö¥ Ïò§Ï∞® ÌóàÏö© (ÏÑ†ÌÉù)
      if (user + "s" === exp || user === exp + "s") return;
      wrongIdxs.push(i);
    });

    const score = expectedTokens.length - wrongIdxs.length;
    setResult({
      wrongIdxs,
      expectedTokens,
      userTokens,
      score,
      total: expectedTokens.length,
    });
    setChecking(true);
  };

  // Í∞ôÏùÄ Î¨∏Ïû• Îã§Ïãú ÏãúÎèÑ
  const handleRetry = () => {
    setRecognized("");
    setResult(null);
    setChecking(false);
    handleSpeak();
  };

  // Í∏∞Î°ù Ï†ÄÏû•
  const saveRecord = () => {
    if (!result) return;
    const rec = {
      type: "SPEAK",
      name: studentName,
      date: new Date().toISOString().slice(0, 10),
      day: dayKey,
      qid: q.id,
      koChunks: q.koChunks,
      enChunks: result.expectedTokens,
      user: recognized,
      userTokens: result.userTokens,
      wrongIdxs: result.wrongIdxs,
      score: result.score,
      totalChunks: result.total,
      ts: Date.now(),
    };
    const prev = JSON.parse(localStorage.getItem("records") || "[]");
    localStorage.setItem("records", JSON.stringify([...prev, rec]));
  };

  // Îã§Ïùå Î¨∏Ïû•ÏúºÎ°ú Ïù¥Îèô (Í∏∞Î°ù Ï†ÄÏû• ÌõÑ)
  const handleNext = () => {
    if (!result) {
      alert("Î®ºÏ†Ä ÎßêÌïòÍ∏∞Î•º ÎàåÎü¨ Ï±ÑÏ†êÌï¥ Ï£ºÏÑ∏Ïöî.");
      return;
    }
    saveRecord();
    if (idx < list.length - 1) {
      setIdx(idx + 1);
      setRecognized("");
      setResult(null);
      setChecking(false);
    } else {
      alert("üéâ Ïò§Îäò ÌïôÏäµ/ÏãúÌóòÏùÑ ÎßàÏ≥§ÏäµÎãàÎã§!");
      nav("/records", { state: { filter: { name: studentName, day: dayKey } } });
    }
  };

  if (!q) {
    return (
      <div className="container">
        <h1 className="title">Î¨∏Ï†úÍ∞Ä ÏóÜÏñ¥Ïöî</h1>
        <div className="btn-group">
          <button className="btn" onClick={() => nav("/")}>
            Ï≤òÏùåÏúºÎ°ú
          </button>
          <button className="btn secondary" onClick={() => nav("/records")}>
            Í∏∞Î°ù Î≥¥Í∏∞
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="container">
      <h1 className="title">
        Î¨∏Ï†ú {idx + 1} / {list.length}
      </h1>

      {/* ÏãúÌóòÏùÄ ÌïúÍ∏ÄÎßå Î≥¥Ïó¨Ï§å */}
      <p className="pill big">
        {q.koChunks?.join(" / ")}
      </p>

      {/* ÎßêÌïòÍ∏∞ & Ï±ÑÏ†ê */}
      <div className="btn-group" style={{ marginTop: 12 }}>
        <button className="btn" onClick={handleSpeak}>ÎßêÌïòÍ∏∞</button>
        <button className="btn secondary" onClick={() => handleCheck()}>Ï±ÑÏ†êÌïòÍ∏∞</button>
        <button className="btn secondary" onClick={handleRetry}>Îã§Ïãú ÎßêÌïòÍ∏∞</button>
        <button className="btn" onClick={handleNext}>Îã§Ïùå Î¨∏Ïû•</button>
      </div>

      {/* Ïù∏Ïãù Í≤∞Í≥º ÌëúÏãú */}
      <div className="panel">
        <div className="panel-title">Ïù∏ÏãùÎêú Î¨∏Ïû•</div>
        <div className="panel-body">{recognized || <span className="muted">ÏïÑÏßÅ Ïù∏ÏãùÎêú Î¨∏Ïû•Ïù¥ ÏóÜÏñ¥Ïöî.</span>}</div>
      </div>

      {/* Ï±ÑÏ†ê Í≤∞Í≥º(ÌÜ†ÌÅ∞Î≥Ñ Ïπ© ÎπÑÍµê) */}
      {checking && result && (
        <div className="panel">
          <div className="panel-title">
            Ï±ÑÏ†ê Í≤∞Í≥º ¬∑ Ï†êÏàò {result.score} / {result.total}
          </div>
          <div className="panel-subtitle">Ï†ïÎãµ(ÏòÅÏñ¥ ÌÜ†ÌÅ∞)</div>
          <div className="chips">
            {result.expectedTokens.map((tok, i) => {
              const wrong = result.wrongIdxs.includes(i);
              return (
                <span key={i} className={`chip ${wrong ? "bad" : "ok"}`}>
                  {tok}
                </span>
              );
            })}
          </div>

          <div className="panel-subtitle" style={{ marginTop: 8 }}>
            ÌïôÏÉù ÌÜ†ÌÅ∞
          </div>
          <div className="chips">
            {result.userTokens.length ? (
              result.userTokens.map((tok, i) => (
                <span key={i} className="chip">
