// src/pages/Exam.js
import React, { useMemo, useState } from "react";
import { useLocation, useNavigate, useParams } from "react-router-dom";
import { QUESTIONS } from "../data";
import { addRecord, uid, updateMasteryOnPerfect } from "../store";
import useSpeech from "../hooks/useSpeech";
import ProgressBar from "../components/ProgressBar";
import Toast from "../components/Toast";

const norm = (s) =>
  s.toLowerCase().replace(/[.,!?;:"'()]/g, "").replace(/\s+/g, " ").trim();
const tokenize = (s) => norm(s).split(" ").filter(Boolean);

export default function Exam() {
  const nav = useNavigate();
  const { day } = useParams();
  const { state } = useLocation(); // { name, date, questions?, originTotal? }
  const name = state?.name || "";
  const date = state?.date || new Date().toISOString().slice(0, 10);

  // Ïò§Îãµ Ïû¨ÏãúÌóòÏù¥Î©¥ state.questions, ÏïÑÎãàÎ©¥ Ï†ÑÏ≤¥
  const baseList = useMemo(
    () => (state?.questions?.length ? state.questions : (QUESTIONS[day] || [])),
    [day, state?.questions]
  );
  const originTotal =
    state?.originTotal || (QUESTIONS[day] || []).length || baseList.length;

  const [idx, setIdx] = useState(0);
  const [heard, setHeard] = useState("");          // Ïù∏ÏãùÎêú Î¨∏Ïû• (ÌëúÏãúÏö©)
  const [colors, setColors] = useState([]);        // Í∞Å ÌÜ†ÌÅ∞Ïπ∏Ïùò Ï†ïÏò§ ÌëúÏãú
  const [badgeToast, setBadgeToast] = useState(null);

  const q = baseList[idx];
  const expectedTokens = useMemo(
    () => tokenize(q?.enChunks?.join(" ") || ""),
    [q]
  );

  // ÎßêÌïòÍ∏∞ ÏãúÏûë/Ï¢ÖÎ£å ‚Üí ÏûêÎèô Ï±ÑÏ†ê
  const { canUseSpeech, listening, start, stop, lastError } = useSpeech({
    lang: "en-US",
    interim: false,
    onResult: (text) => {
      setHeard(text);
      autoScore(text);
    },
  });

  if (!q) {
    return (
      <div className="container">
        <div className="card">
          <h2>Î¨∏Ï†úÍ∞Ä ÏóÜÏñ¥Ïöî</h2>
          <div className="nav">
            <button className="btn" onClick={() => nav("/")}>Ï≤òÏùåÏúºÎ°ú</button>
          </div>
        </div>
      </div>
    );
  }

  // Ï†ïÎãµ Î¨∏Ïû•ÏùÄ ÌôîÎ©¥Ïóê Ï†àÎåÄ Ï∂úÎ†•ÌïòÏßÄ ÏïäÎäîÎã§.
  // ÎåÄÏã† "ÌÜ†ÌÅ∞Ïπ∏(‚ñ†)"ÏùÑ Í∏∞ÎåÄ ÌÜ†ÌÅ∞ Í∞úÏàòÎßåÌÅº Í∑∏Î¶¨Í≥†, ÎßûÏúºÎ©¥ Ï¥àÎ°ù, ÌãÄÎ¶¨Î©¥ Îπ®Í∞ïÏúºÎ°ú ÌëúÏãú.
  const autoScore = (text) => {
    const userTokens = tokenize(text);
    const colorArr = expectedTokens.map((tk, i) => {
      const user = userTokens[i] || "";
      const ok = user === tk || user + "s" === tk || user === tk + "s";
      return ok; // true(Ï¥àÎ°ù) / false(Îπ®Í∞ï)
    });
    setColors(colorArr);
  };

  const handleNext = () => {
    // Ï†êÏàò Ï†ÄÏû• + Î±ÉÏßÄ/ÏàôÎ†®ÎèÑ
    const userTokens = tokenize(heard);
    const wrongIdxs = [];
    expectedTokens.forEach((tk, i) => {
      const user = userTokens[i] || "";
      if (!(user === tk || user + "s" === tk || user === tk + "s")) wrongIdxs.push(i);
    });
    const score = expectedTokens.length - wrongIdxs.length;

    const rec = {
      id: uid(),
      type: "SPEAK",
      name, date, day,
      qid: q.id,
      koChunks: q.koChunks,
      enChunks: expectedTokens,  // Ï†ÄÏû•ÏùÄ ÌïòÎêò ÌôîÎ©¥Ïóî ÎÖ∏Ï∂úÌïòÏßÄ ÏïäÏùå
      full: q.full,
      user: heard,
      wrongIdxs,
      totalChunks: expectedTokens.length,
      score,
      ts: Date.now(),
    };
    addRecord(rec);

    // ÏôÑÎ≤Ω Ï†ïÎãµÏù¥Î©¥ ÏàôÎ†®ÎèÑ ‚Üí Day Î±ÉÏßÄ Ï≤¥ÌÅ¨
    if (wrongIdxs.length === 0) {
      const granted = updateMasteryOnPerfect(name, day, q.id, originTotal);
      if (granted) setBadgeToast(`üéâ ${name} ‚Äî ${String(day).toUpperCase()} Î±ÉÏßÄ ÌöçÎìù!`);
    }

    // Îã§Ïùå Î¨∏Ìï≠
    setHeard("");
    setColors([]);
    if (idx < baseList.length - 1) {
      setIdx((i) => i + 1);
    } else {
      nav("/records", { state: { name, day } });
    }
  };

  return (
    <div className="container">
      <div className="card">
        <h1 className="title">ÏãúÌóò ¬∑ Î¨∏Ï†ú {idx + 1} / {baseList.length}</h1>

        {/* Ïò§Îãµ Ïû¨ÏãúÌóòÏùº ÎïåÎßå ÏßÑÌñâÎ•† Î∞î */}
        {state?.questions?.length ? (
          <div style={{ margin: "6px 0 12px" }}>
            <ProgressBar now={idx} total={baseList.length} />
          </div>
        ) : null}

        {/* ÌïúÍ∏Ä ÌîÑÎ°¨ÌîÑÌä∏Îßå ÎÖ∏Ï∂ú */}
        <p className="yellow">{q.koChunks.join(" / ")}</p>

        {/* ÌÜ†ÌÅ∞Ïπ∏(Ï†ïÎãµ ÎπÑÍ≥µÍ∞ú): Í∏∞ÎåÄ ÌÜ†ÌÅ∞ Í∞úÏàòÎßåÌÅº ÎÑ§Î™®Ïπ∏ÏùÑ Í∑∏Î¶¨Í≥† Ï†ïÏò§ÏÉâ ÌëúÏãú */}
        <div className="row" style={{ gap: 6, flexWrap: "wrap", margin: "14px 0 8px" }}>
          {expectedTokens.map((_, i) => (
            <span
              key={i}
              style={{
                display: "inline-block",
                width: 16, height: 16, borderRadius: 4,
                background: colors[i] === undefined
                  ? "#2b3442" // ÏïÑÏßÅ ÎØ∏Ï±ÑÏ†ê(ÌöåÏÉâ)
                  : colors[i]
                  ? "rgba(34,197,94,.9)" // Ï¥àÎ°ù
                  : "rgba(239,68,68,.9)",   // Îπ®Í∞ï
              }}
              title={`token ${i + 1}`}
            />
          ))}
        </div>

        {/* Ïù∏ÏãùÎêú Î¨∏Ïû•(ÌïôÏÉù ÌîºÎìúÎ∞±Ïö©) ‚Äì ÏûÖÎ†•Ï∞Ω Ï†úÍ±∞, Îã®Ïàú ÌëúÏãúÎßå */}
        <div
          style={{
            width: "100%", minHeight: 56, borderRadius: 12,
            background: "#0b1420", border: "1px solid #243042",
            padding: "12px 14px", color: "#cbd5e1", marginTop: 8
          }}
        >
          {heard || <span style={{ opacity: .5 }}>ÎßêÌïòÍ∏∞ Î≤ÑÌäºÏùÑ ÎàåÎü¨ ÎãµÌïòÏÑ∏Ïöî</span>}
        </div>

        {/* ÎßàÏù¥ÌÅ¨ Ïª®Ìä∏Î°§Îßå Ï†úÍ≥µ (ÌÉÄÏù¥Ìïë Ï†úÍ±∞) */}
        <div className="nav">
          {canUseSpeech ? (
            listening
              ? <button className="btn danger" onClick={stop}>Ï†ïÏßÄ</button>
              : <button className="btn primary" onClick={() => { setHeard(""); setColors([]); start(); }}>ÎßêÌïòÍ∏∞</button>
          ) : (
            <button className="btn danger" disabled>ÎßàÏù¥ÌÅ¨Î•º ÏÇ¨Ïö©Ìï† Ïàò ÏóÜÏñ¥Ïöî</button>
          )}
          <button className="btn" onClick={() => { setHeard(""); setColors([]); }}>Îã§Ïãú ÎßêÌïòÍ∏∞</button>
          <button className="btn" onClick={handleNext} disabled={!heard}>Îã§Ïùå Î¨∏Ïû•</button>
          <button className="btn" onClick={() => nav("/records", { state: { name, day } })}>Í∏∞Î°ù Î≥¥Í∏∞</button>
        </div>

        {/* ÎßàÏù¥ÌÅ¨ ÏóêÎü¨ ÌëúÍ∏∞(Í∂åÌïú/HTTPS Îì±) */}
        {lastError && (
          <div style={{ marginTop: 8, color: "#ef4444", fontSize: 13 }}>
            ÎßàÏù¥ÌÅ¨ Ïò§Î•ò: {lastError}
          </div>
        )}
      </div>

      {badgeToast && <Toast text={badgeToast} onDone={() => setBadgeToast(null)} />}
    </div>
  );
}
