// src/pages/Records.js
import React, { useEffect, useMemo, useState } from "react";
import { useNavigate } from "react-router-dom";
import { QUESTIONS } from "../data";
import { isAdminSession } from "../auth";

const getRecords = () => {
  try {
    return JSON.parse(localStorage.getItem("records") || "[]");
  } catch {
    return [];
  }
};

const saveRecords = (arr) => {
  localStorage.setItem("records", JSON.stringify(arr));
};

// 문자열 정규화 & 토큰화 (채점/표시에 사용)
const norm = (s) =>
  (s || "")
    .toLowerCase()
    .replace(/[^\w\s]/g, "")
    .replace(/\s+/g, " ")
    .trim();

const tok = (s) => norm(s).split(" ").filter(Boolean);

// 토큰 단위로 정답/오답 색상 렌더
function TokenDiff({ expected, wrongIdxs }) {
  const t = tok(expected);
  const set = new Set(wrongIdxs || []);
  return (
    <div style={{ display: "flex", gap: 6, flexWrap: "wrap" }}>
      {t.map((w, i) => (
        <span
          key={i}
          style={{
            padding: "2px 6px",
            borderRadius: 6,
            background: set.has(i) ? "rgba(239,68,68,.15)" : "rgba(34,197,94,.15)",
            color: set.has(i) ? "#ef4444" : "#22c55e",
            fontWeight: 700,
          }}
        >
          {w}
        </span>
      ))}
    </div>
  );
}

export default function Records() {
  const nav = useNavigate();
  const admin = isAdminSession();

  const [raw, setRaw] = useState(getRecords());
  const [student, setStudent] = useState("ALL");
  const [day, setDay] = useState("ALL");
  const [onlyWrong, setOnlyWrong] = useState(false);
  const [openMap, setOpenMap] = useState({}); // 상세 토글

  // 최신순 정렬
  const ordered = useMemo(() => {
    return [...raw].sort((a, b) => (b.ts || 0) - (a.ts || 0));
  }, [raw]);

  // 학생/Day 목록
  const students = useMemo(() => {
    const s = new Set(ordered.map((r) => r.name).filter(Boolean));
    return ["ALL", ...Array.from(s).sort((a, b) => a.localeCompare(b))];
  }, [ordered]);

  const days = useMemo(() => {
    const s = new Set(ordered.map((r) => r.day).filter(Boolean));
    const arr = Array.from(s);
    arr.sort((a, b) => {
      const na = parseInt(String(a).replace(/\D/g, "")) || 0;
      const nb = parseInt(String(b).replace(/\D/g, "")) || 0;
      return na - nb;
    });
    return ["ALL", ...arr];
  }, [ordered]);

  // 필터링
  const list = useMemo(() => {
    return ordered.filter((r) => {
      if (student !== "ALL" && r.name !== student) return false;
      if (day !== "ALL" && r.day !== day) return false;
      if (onlyWrong && (!r.wrongIdxs || r.wrongIdxs.length === 0)) return false;
      return true;
    });
  }, [ordered, student, day, onlyWrong]);

  // Day → 총 문항수(마스터리 계산 등에 사용 가능)
  const dayTotalMap = useMemo(() => {
    const m = {};
    Object.keys(QUESTIONS || {}).forEach((d) => {
      m[d] = (QUESTIONS[d] || []).length;
    });
    return m;
  }, []);

  // 개별 기록 삭제(관리자 전용)
  const removeOne = (ts) => {
    if (!admin) return;
    if (!window.confirm("이 기록을 삭제할까요?")) return;
    const next = raw.filter((r) => r.ts !== ts);
    setRaw(next);
    saveRecords(next);
  };

  // JSON 내보내기(관리자 전용)
  const exportJSON = () => {
    const blob = new Blob([JSON.stringify(raw, null, 2)], { type: "application/json" });
    const url = URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url;
    const today = new Date().toISOString().slice(0, 10);
    a.download = `records-${today}.json`;
    a.click();
    URL.revokeObjectURL(url);
  };

  // 상세 토글
  const toggleOpen = (key) =>
    setOpenMap((m) => ({
      ...m,
      [key]: !m[key],
    }));

  // 빈 상태
  if (!raw.length) {
    return (
      <div className="container">
        <div className="card">
          <h1 className="title">기록 보기</h1>
          <p className="muted">아직 저장된 기록이 없어요.</p>
          <div className="nav">
            <button className="btn" onClick={() => nav("/")}>
              처음으로
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="container">
      <div className="card" style={{ maxWidth: 1100 }}>
        <h1 className="title">기록 보기</h1>

        {/* 필터 바 */}
        <div
          className="row"
          style={{
            flexWrap: "wrap",
            gap: 12,
            marginBottom: 12,
            alignItems: "center",
          }}
        >
          <div className="row" style={{ gap: 8 }}>
            <label className="muted" htmlFor="studentSel">
              학생
            </label>
            <select
              id="studentSel"
              value={student}
              onChange={(e) => setStudent(e.target.value)}
              className="btn"
              style={{ minWidth: 160 }}
            >
              {students.map((s) => (
                <option key={s} value={s}>
                  {s === "ALL" ? "전체" : s}
                </option>
              ))}
            </select>
          </div>

          <div className="row" style={{ gap: 8 }}>
            <label className="muted" htmlFor="daySel">
              Day
            </label>
            <select
              id="daySel"
              value={day}
              onChange={(e) => setDay(e.target.value)}
              className="btn"
              style={{ minWidth: 120 }}
            >
              {days.map((d) => (
                <option key={d} value={d}>
                  {d === "ALL" ? "전체" : String(d).toUpperCase()}
                </option>
              ))}
            </select>
          </div>

          <label className="row" style={{ gap: 8, cursor: "pointer" }}>
            <input
              type="checkbox"
              checked={onlyWrong}
              onChange={(e) => setOnlyWrong(e.target.checked)}
            />
            <span className="muted">오답만</span>
          </label>

          <div style={{ flex: 1 }} />

          <button className="btn" onClick={() => nav("/")}>
            처음으로
          </button>

          {admin && (
            <button className="btn" onClick={exportJSON} title="JSON 내보내기">
              내보내기(JSON)
            </button>
          )}
        </div>

        {/* 리스트 (세로) */}
        <div style={{ display: "flex", flexDirection: "column", gap: 12 }}>
          {list.map((r) => {
            const key = String(r.ts);
            const pct =
              r.totalChunks && r.totalChunks > 0
                ? Math.round(((r.score || 0) / r.totalChunks) * 100)
                : 0;

            return (
              <div
                key={key}
                style={{
                  border: "1px solid #2e2e2e",
                  borderRadius: 12,
                  padding: 12,
                  background: "rgba(255,255,255,0.02)",
                }}
              >
                {/* 헤더 줄 */}
                <div
                  style={{
                    display: "grid",
                    gridTemplateColumns: "1fr auto",
                    gap: 8,
                    alignItems: "center",
                  }}
                >
                  <div style={{ display: "flex", flexWrap: "wrap", gap: 12, alignItems: "baseline" }}>
                    <div style={{ fontWeight: 800 }}>
                      {r.type || "SPEAK"} · Day {String(r.day).toUpperCase()} · Q{r.qid ?? "?"}
                    </div>
                    <div className="muted">
                      {r.date} ({new Date(r.ts || Date.now()).toLocaleTimeString()})
                    </div>
                    <div style={{ fontWeight: 800 }}>
                      점수: {r.score ?? 0} / {r.totalChunks ?? 0} ({pct}%)
                    </div>
                  </div>

                  <div className="row" style={{ gap: 8 }}>
                    <button className="btn" onClick={() => toggleOpen(key)}>
                      {openMap[key] ? "접기" : "내 답 보기"}
                    </button>
                    {admin && (
                      <button className="btn danger" onClick={() => removeOne(r.ts)}>
                        삭제
                      </button>
                    )}
                  </div>
                </div>

                {/* 펼침 상세 */}
                {openMap[key] && (
                  <div style={{ marginTop: 10, display: "grid", gap: 8 }}>
                    <div className="muted">정답 문장</div>
                    <TokenDiff expected={r.full} wrongIdxs={r.wrongIdxs} />

                    <div className="muted" style={{ marginTop: 6 }}>
                      학생 답안
                    </div>
                    <div className="yellow" style={{ wordBreak: "break-word" }}>
                      {r.user || "(빈 값)"}
                    </div>

                    {/* 보조 정보 */}
                    <div className="muted" style={{ marginTop: 6, fontSize: 12 }}>
                      학습 Day 총 문항: {dayTotalMap[r.day] ?? "-"}
                    </div>
                  </div>
                )}
              </div>
            );
          })}
        </div>

        {/* 꼬리 버튼 */}
        <div className="nav" style={{ marginTop: 16 }}>
          <button className="btn" onClick={() => nav("/")}>
            처음으로
          </button>
        </div>
      </div>
    </div>
  );
}
