// src/pages/Exam.js
import React, { useMemo, useRef, useState } from "react";
import { useLocation, useNavigate, useParams } from "react-router-dom";
import { QUESTIONS } from "../data";
import { addRecord, uid, updateMasteryOnPerfect } from "../store";
import useSpeech from "../hooks/useSpeech";
import ProgressBar from "../components/ProgressBar";
import Toast from "../components/Toast";

const norm = (s) =>
  s.toLowerCase().replace(/[.,!?;:"'()]/g, "").replace(/\s+/g, " ").trim();
const tokenize = (s) => norm(s).split(" ").filter(Boolean);

export default function Exam() {
  const nav = useNavigate();
  const { day } = useParams();
  const { state } = useLocation(); // { name, date, questions?, originTotal? }
  const name = state?.name || "";
  const date = state?.date || new Date().toISOString().slice(0,10);

  // 재시험(오답만) → state.questions / 일반 → QUESTIONS[day]
  const baseList = useMemo(() => {
    if (state?.questions?.length) return state.questions;
    return QUESTIONS[day] || [];
  }, [day, state?.questions]);

  const originTotal = state?.originTotal || (QUESTIONS[day]||[]).length || baseList.length;

  const [idx, setIdx] = useState(0);
  const [ans, setAns] = useState("");
  const [badgeToast, setBadgeToast] = useState(null); // string or null
  const q = baseList[idx];

  const inputRef = useRef(null);

  const { canUseSpeech, listening, start, stop } = useSpeech({
    lang:"en-US",
    onResult: (text) => setAns(text),
  });

  if (!q) {
    return (
      <div className="container">
        <div className="card">
          <h2>문제가 없어요</h2>
          <div className="nav"><button className="btn" onClick={()=>nav("/")}>처음으로</button></div>
        </div>
      </div>
    );
  }

  const expectedTokens = tokenize(q.enChunks.join(" "));
  const userTokens = tokenize(ans);
  const colored = expectedTokens.map((tk,i)=>{
    const user = userTokens[i] || "";
    const ok = user===tk || user+"s"===tk || user===tk+"s";
    return { tk, ok };
  });

  const handleScore = () => {
    const wrongIdxs = [];
    expectedTokens.forEach((tk,i)=>{
      const user = userTokens[i] || "";
      if (user===tk || user+"s"===tk || user===tk+"s") return;
      wrongIdxs.push(i);
    });
    const score = expectedTokens.length - wrongIdxs.length;

    const rec = {
      id: uid(),
      type: "SPEAK",
      name, date, day,
      qid: q.id,
      koChunks: q.koChunks,
      enChunks: expectedTokens,
      full: q.full,
      user: ans,
      wrongIdxs,
      totalChunks: expectedTokens.length,
      score,
      ts: Date.now(),
    };
    addRecord(rec);

    // 완벽 통과 시 숙련도/뱃지
    if (wrongIdxs.length === 0) {
      const granted = updateMasteryOnPerfect(name, day, q.id, originTotal);
      if (granted) setBadgeToast(`🎉 ${name} — ${String(day).toUpperCase()} 뱃지 획득!`);
    }

    setAns("");
    if (idx < baseList.length - 1) {
      setIdx(i => i + 1);
      setTimeout(()=>inputRef.current?.focus(), 0);
    } else {
      nav("/records", { state: { name, day } });
    }
  };

  return (
    <div className="container">
      <div className="card">
        <h1 className="title">시험 · 문제 {idx+1} / {baseList.length}</h1>
        {/* 오답 재시험 진행률 */}
        {state?.questions?.length ? (
          <div style={{margin:"6px 0 12px"}}>
            <ProgressBar now={idx} total={baseList.length}/>
          </div>
        ) : null}

        <p className="yellow">{q.koChunks.join(" / ")}</p>

        {/* 실시간 컬러링 */}
        <div className="row" style={{gap:8, flexWrap:"wrap", margin:"12px 0"}}>
          {colored.map(({tk, ok}, i)=>(
            <span key={i} className={ok ? "word-ok" : "word-bad"}>{tk}</span>
          ))}
        </div>

        {/* 인풋 + 마이크(문장 듣기 버튼 없음) */}
        <input
          ref={inputRef}
          value={ans}
          onChange={e=>setAns(e.target.value)}
          placeholder="말하기(마이크) 또는 타이핑"
          onKeyDown={(e)=>{ if (e.key==="Enter") handleScore(); }}
        />

        <div className="nav">
          {canUseSpeech && (
            listening
              ? <button className="btn danger" onClick={stop}>정지</button>
              : <button className="btn primary" onClick={start}>말하기</button>
          )}
          <button className="btn" onClick={()=>setAns("")}>지우기</button>
          <button className="btn" onClick={handleScore}>채점하기</button>
          <button className="btn" onClick={()=>nav("/records", { state: { name, day } })}>기록 보기</button>
        </div>
      </div>

      {badgeToast && (
        <Toast text={badgeToast} onDone={()=>setBadgeToast(null)} />
      )}
    </div>
  );
}
